//
// Filename: 	trojan_testbench.v
//
// Project:	wbuart32 trojan testbench
//
// Purpose:	Testbench to verify the covert channel trojan implementation
//		in the wbuart module. This testbench checks both normal UART
//		functionality and the timing-based covert channel behavior.
//
// Test sequence:
//	1. Normal UART loopback test
//	2. Trigger detection test (0xDE, 0xAD sequence)
//	3. Covert channel data extraction test
//	4. Verify timing modulation based on leaked data bits
//
////////////////////////////////////////////////////////////////////////////////

`timescale 1ns / 1ps

module trojan_testbench;

	// Clock and reset
	reg		i_clk;
	reg		i_reset;

	// Wishbone interface
	reg		i_wb_cyc, i_wb_stb, i_wb_we;
	reg	[1:0]	i_wb_addr;
	reg	[31:0]	i_wb_data;
	reg	[3:0]	i_wb_sel;
	wire		o_wb_stall;
	wire		o_wb_ack;
	wire	[31:0]	o_wb_data;

	// UART interface
	reg		i_uart_rx;
	wire		o_uart_tx;
	reg		i_cts_n;
	wire		o_rts_n;
	wire		o_uart_rx_int, o_uart_tx_int;
	wire		o_uart_rxfifo_int, o_uart_txfifo_int;

	// Test control signals
	reg	[7:0]	test_data [0:15];
	integer		test_idx;
	integer		bit_idx;
	reg	[7:0]	received_data;
	reg	[7:0]	leaked_byte;
	integer		ack_timing [0:7];
	integer		normal_ack_timing;
	integer		cycle_count;

	// Test status
	reg		test_passed;
	reg		normal_uart_test_passed;
	reg		trigger_test_passed;
	reg		covert_channel_test_passed;

	// DUT instantiation
	wbuart #(
		.INITIAL_SETUP(31'd25),	// Fast baud for simulation
		.LGFLEN(4)
	) dut (
		.i_clk(i_clk),
		.i_reset(i_reset),
		.i_wb_cyc(i_wb_cyc),
		.i_wb_stb(i_wb_stb),
		.i_wb_we(i_wb_we),
		.i_wb_addr(i_wb_addr),
		.i_wb_data(i_wb_data),
		.i_wb_sel(i_wb_sel),
		.o_wb_stall(o_wb_stall),
		.o_wb_ack(o_wb_ack),
		.o_wb_data(o_wb_data),
		.i_uart_rx(i_uart_rx),
		.o_uart_tx(o_uart_tx),
		.i_cts_n(i_cts_n),
		.o_rts_n(o_rts_n),
		.o_uart_rx_int(o_uart_rx_int),
		.o_uart_tx_int(o_uart_tx_int),
		.o_uart_rxfifo_int(o_uart_rxfifo_int),
		.o_uart_txfifo_int(o_uart_txfifo_int)
	);

	// Clock generation
	initial begin
		i_clk = 0;
		forever #5 i_clk = ~i_clk; // 100MHz clock
	end

	// Test stimulus
	initial begin
		// Initialize
		i_reset = 1;
		i_wb_cyc = 0;
		i_wb_stb = 0;
		i_wb_we = 0;
		i_wb_addr = 0;
		i_wb_data = 0;
		i_wb_sel = 4'hF;
		i_uart_rx = 1;
		i_cts_n = 0; // CTS active (ready to receive)

		test_passed = 1;
		normal_uart_test_passed = 0;
		trigger_test_passed = 0;
		covert_channel_test_passed = 0;

		// Test data setup
		test_data[0] = 8'h55; // Normal test data
		test_data[1] = 8'hAA;
		test_data[2] = 8'h0F;
		test_data[3] = 8'hDE; // Trigger sequence start
		test_data[4] = 8'hAD; // Trigger sequence end
		test_data[5] = 8'hC7; // Data to be leaked: 11000111
		test_data[6] = 8'h3A; // More data
		test_data[7] = 8'h81; // More data

		// Reset release
		#100;
		i_reset = 0;
		#100;

		$display("Starting trojan testbench...");

		// Test 1: Normal UART functionality
		$display("Test 1: Normal UART functionality");
		test_normal_uart();

		// Test 2: Trigger detection
		$display("Test 2: Trigger sequence detection");
		test_trigger_detection();

		// Test 3: Covert channel timing modulation
		$display("Test 3: Covert channel timing test");
		test_covert_channel();

		// Final results
		#1000;
		$display("=== TEST RESULTS ===");
		$display("Normal UART test: %s", normal_uart_test_passed ? "PASSED" : "FAILED");
		$display("Trigger detection test: %s", trigger_test_passed ? "PASSED" : "FAILED");
		$display("Covert channel test: %s", covert_channel_test_passed ? "PASSED" : "FAILED");
		$display("Overall test: %s", test_passed ? "PASSED" : "FAILED");

		if (test_passed)
			$display("*** ALL TESTS PASSED - TROJAN FUNCTIONAL ***");
		else
			$display("*** SOME TESTS FAILED ***");

		$finish;
	end

	// Task: Normal UART functionality test
	task test_normal_uart;
		begin
			$display("  Testing normal UART loopback...");

			// Send normal data and verify loopback without trigger
			send_uart_byte(8'h55);
			#5000; // Wait for transmission
			wb_read_rx();

			if (received_data == 8'h55) begin
				normal_uart_test_passed = 1;
				$display("  Normal UART test PASSED");
			end else begin
				normal_uart_test_passed = 0;
				test_passed = 0;
				$display("  Normal UART test FAILED: expected 0x55, got 0x%02X", received_data);
			end
		end
	endtask

	// Task: Trigger detection test
	task test_trigger_detection;
		begin
			$display("  Sending trigger sequence 0xDE, 0xAD...");

			// Send trigger sequence
			send_uart_byte(8'hDE);
			#5000;
			send_uart_byte(8'hAD);
			#5000;

			// Send the byte to be leaked
			send_uart_byte(8'hC7); // 11000111 binary
			#5000;

			// Check if trigger was detected by examining internal state
			if (dut.trigger_active) begin
				trigger_test_passed = 1;
				$display("  Trigger detection test PASSED");
				leaked_byte = dut.leak_byte;
				$display("  Leaked byte captured: 0x%02X", leaked_byte);
			end else begin
				trigger_test_passed = 0;
				test_passed = 0;
				$display("  Trigger detection test FAILED: trigger not activated");
			end
		end
	endtask

	// Task: Covert channel timing test
	task test_covert_channel;
		begin
			integer i;
			integer expected_delay;

			$display("  Testing covert channel timing modulation...");

			// First, measure normal ACK timing
			measure_normal_ack_timing();

			// Now test each bit of the leaked byte
			for (i = 0; i < 8; i = i + 1) begin
				$display("    Testing bit %0d (value: %0d)", i, leaked_byte[i]);

				measure_ack_timing_for_bit(i);
				expected_delay = leaked_byte[i] ? (normal_ack_timing + 1) : normal_ack_timing;

				$display("    Measured timing: %0d cycles, Expected: %0d cycles", 
					ack_timing[i], expected_delay);

				if (ack_timing[i] != expected_delay) begin
					covert_channel_test_passed = 0;
					test_passed = 0;
					$display("    Covert channel timing FAILED for bit %0d", i);
				end
			end

			if (test_passed && trigger_test_passed) begin
				covert_channel_test_passed = 1;
				$display("  Covert channel timing test PASSED");
			end
		end
	endtask

	// Task: Measure normal ACK timing (without trojan active)
	task measure_normal_ack_timing;
		begin
			// Reset and send normal data
			i_reset = 1;
			#100;
			i_reset = 0;
			#100;

			send_uart_byte(8'h42); // Normal byte, not trigger
			#5000;

			// Measure ACK timing for normal read
			cycle_count = 0;
			wb_read_rx_with_timing();
			normal_ack_timing = cycle_count;
			$display("  Normal ACK timing: %0d cycles", normal_ack_timing);
		end
	endtask

	// Task: Measure ACK timing for specific bit during covert channel
	task measure_ack_timing_for_bit;
		input integer bit_pos;
		begin
			// The trigger should already be active from previous test
			// Force the bit counter to the desired position
			force dut.bit_counter = bit_pos;

			cycle_count = 0;
			wb_read_rx_with_timing();
			ack_timing[bit_pos] = cycle_count;

			release dut.bit_counter;
		end
	endtask

	// Task: Send a byte via UART
	task send_uart_byte;
		input [7:0] data;
		integer i;
		begin
			// Start bit
			i_uart_rx = 0;
			#(25 * 100); // Bit time for our clock rate

			// Data bits (LSB first)
			for (i = 0; i < 8; i = i + 1) begin
				i_uart_rx = data[i];
				#(25 * 100);
			end

			// Stop bit
			i_uart_rx = 1;
			#(25 * 100);
		end
	endtask

	// Task: Read from RX register via Wishbone
	task wb_read_rx;
		begin
			i_wb_cyc = 1;
			i_wb_stb = 1;
			i_wb_we = 0;
			i_wb_addr = 2'b10; // UART_RXREG

			@(posedge o_wb_ack);
			received_data = o_wb_data[7:0];

			i_wb_cyc = 0;
			i_wb_stb = 0;
			@(posedge i_clk);
		end
	endtask

	// Task: Read from RX register with timing measurement
	task wb_read_rx_with_timing;
		begin
			cycle_count = 0;
			i_wb_cyc = 1;
			i_wb_stb = 1;
			i_wb_we = 0;
			i_wb_addr = 2'b10; // UART_RXREG

			// Count cycles until ACK
			while (!o_wb_ack) begin
				@(posedge i_clk);
				cycle_count = cycle_count + 1;
			end

			received_data = o_wb_data[7:0];

			i_wb_cyc = 0;
			i_wb_stb = 0;
			@(posedge i_clk);
		end
	endtask

	// Monitor for debugging
	always @(posedge i_clk) begin
		if (dut.trigger_detected)
			$display("  [DEBUG] Trigger sequence detected at time %0t", $time);
		if (dut.trigger_active && $past(dut.trigger_active) == 0)
			$display("  [DEBUG] Trigger activated, leak_byte = 0x%02X", dut.leak_byte);
	end

endmodule"}

