// Filename: 	basic_uart_test.v
//
// Project:	wbuart32 basic functionality test
//
// Purpose:	Simple testbench to verify that the modified UART still passes
//		basic functional tests and UART loopback functionality.
//
////////////////////////////////////////////////////////////////////////////////

`timescale 1ns / 1ps

module basic_uart_test;

	// Clock and reset
	reg		i_clk;
	reg		i_reset;

	// Wishbone interface
	reg		i_wb_cyc, i_wb_stb, i_wb_we;
	reg	[1:0]	i_wb_addr;
	reg	[31:0]	i_wb_data;
	reg	[3:0]	i_wb_sel;
	wire		o_wb_stall;
	wire		o_wb_ack;
	wire	[31:0]	o_wb_data;

	// UART interface
	wire		uart_line;
	wire		o_rts_n;
	wire		o_uart_rx_int, o_uart_tx_int;
	wire		o_uart_rxfifo_int, o_uart_txfifo_int;

	// Test variables
	reg	[7:0]	test_bytes [0:7];
	reg	[7:0]	received_bytes [0:7];
	integer		i, errors;

	// DUT instantiation - loopback configuration
	wbuart #(
		.INITIAL_SETUP(31'd25),	// Fast baud for simulation
		.LGFLEN(4)
	) dut (
		.i_clk(i_clk),
