		.o_wb_stall(o_wb_stall),
		.o_wb_ack(o_wb_ack),
		.o_wb_data(o_wb_data),
		.i_uart_rx(uart_line),    // Connect RX to TX for loopback
		.o_uart_tx(uart_line),    // Connect TX to RX for loopback
		.i_cts_n(1'b0),          // CTS active
		.o_rts_n(o_rts_n),
		.o_uart_rx_int(o_uart_rx_int),
		.o_uart_tx_int(o_uart_tx_int),
		.o_uart_rxfifo_int(o_uart_rxfifo_int),
		.o_uart_txfifo_int(o_uart_txfifo_int)
	);

	// Clock generation
	initial begin
		i_clk = 0;
		forever #5 i_clk = ~i_clk; // 100MHz clock
	end

	// Test stimulus
	initial begin
		// Initialize
		i_reset = 1;
		i_wb_cyc = 0;
		i_wb_stb = 0;
		i_wb_we = 0;
		i_wb_addr = 0;
		i_wb_data = 0;
		i_wb_sel = 4'hF;
		errors = 0;

		// Test data
		test_bytes[0] = 8'h55;
		test_bytes[1] = 8'hAA;
		test_bytes[2] = 8'h0F;
		test_bytes[3] = 8'hF0;
		test_bytes[4] = 8'h12;
		test_bytes[5] = 8'h34;
		test_bytes[6] = 8'h56;
		test_bytes[7] = 8'h78;

		// Reset release
		#100;
		i_reset = 0;
		#100;

		$display("Starting basic UART loopback test...");

		// Send test bytes
		for (i = 0; i < 8; i = i + 1) begin
			$display("Sending byte %0d: 0x%02X", i, test_bytes[i]);
			wb_write_tx(test_bytes[i]);
