			wb_read_rx();
			received_bytes[i] = o_wb_data[7:0];
			$display("Received byte %0d: 0x%02X", i, received_bytes[i]);

			if (received_bytes[i] != test_bytes[i]) begin
				$display("ERROR: Mismatch at byte %0d - sent 0x%02X, received 0x%02X", 
					i, test_bytes[i], received_bytes[i]);
				errors = errors + 1;
			end
		end

		// Results
		#1000;
		if (errors == 0) begin
			$display("*** BASIC UART LOOPBACK TEST PASSED ***");
			$display("All %0d bytes transmitted and received correctly", 8);
		end else begin
			$display("*** BASIC UART LOOPBACK TEST FAILED ***");
			$display("%0d errors detected out of %0d bytes", errors, 8);
		end

		$finish;
	end

	// Task: Write to TX register via Wishbone
	task wb_write_tx;
		input [7:0] data;
		begin
			i_wb_cyc = 1;
			i_wb_stb = 1;
			i_wb_we = 1;
			i_wb_addr = 2'b11; // UART_TXREG
			i_wb_data = {24'h0, data};

			@(posedge o_wb_ack);

			i_wb_cyc = 0;
			i_wb_stb = 0;
			i_wb_we = 0;
			@(posedge i_clk);
		end
	endtask

	// Task: Read from RX register via Wishbone
	task wb_read_rx;
		begin
			// Wait for RX interrupt (data available)
			wait(o_uart_rx_int);

			i_wb_cyc = 1;
			i_wb_stb = 1;
			i_wb_we = 0;
			i_wb_addr = 2'b10; // UART_RXREG

			@(posedge o_wb_ack);

			i_wb_cyc = 0;
			i_wb_stb = 0;
			@(posedge i_clk);
		end
	endtask

endmodule", "filePath": "c:\Users\Ananthan\OneDrive\Desktop\CSAW AI hardware\challenge2\hard\basic_uart_test.v"}

