//
// Filename: 	simple_trojan_test.v
//
// Project:	Simple trojan functionality test
//
// Purpose:	A simplified testbench to verify the covert channel trojan
//		implementation works correctly.
//
////////////////////////////////////////////////////////////////////////////////

`timescale 1ns / 1ps

module simple_trojan_test;

	// Clock and reset
	reg		i_clk;
	reg		i_reset;

	// Wishbone interface
	reg		i_wb_cyc, i_wb_stb, i_wb_we;
	reg	[1:0]	i_wb_addr;
	reg	[31:0]	i_wb_data;
	reg	[3:0]	i_wb_sel;
	wire		o_wb_stall;
	wire		o_wb_ack;
	wire	[31:0]	o_wb_data;

	// UART interface
	reg		i_uart_rx;
	wire		o_uart_tx;
	reg		i_cts_n;
	wire		o_rts_n;
	wire		o_uart_rx_int, o_uart_tx_int;
	wire		o_uart_rxfifo_int, o_uart_txfifo_int;

	// Test variables
	integer		cycle_count;
	integer		ack_cycles;
	reg	[7:0]	test_leak_byte;

	// DUT instantiation
	wbuart #(
		.INITIAL_SETUP(31'd25),	// Fast baud for simulation
		.LGFLEN(4)
	) dut (
		.i_clk(i_clk),
		.i_reset(i_reset),
		.i_wb_cyc(i_wb_cyc),
		.i_wb_stb(i_wb_stb),
		.i_wb_we(i_wb_we),
		.i_wb_addr(i_wb_addr),
		.i_wb_data(i_wb_data),
		.i_wb_sel(i_wb_sel),
		.o_wb_stall(o_wb_stall),
		.o_wb_ack(o_wb_ack),
		.o_wb_data(o_wb_data),
		.i_uart_rx(i_uart_rx),
		.o_uart_tx(o_uart_tx),
		.i_cts_n(i_cts_n),
		.o_rts_n(o_rts_n),
		.o_uart_rx_int(o_uart_rx_int),
		.o_uart_tx_int(o_uart_tx_int),
		.o_uart_rxfifo_int(o_uart_rxfifo_int),
		.o_uart_txfifo_int(o_uart_txfifo_int)
	);

	// Clock generation
	initial begin
		i_clk = 0;
		forever #5 i_clk = ~i_clk; // 100MHz clock
	end

	// Test stimulus
	initial begin
		// Initialize
		i_reset = 1;
		i_wb_cyc = 0;
		i_wb_stb = 0;
		i_wb_we = 0;
		i_wb_addr = 0;
		i_wb_data = 0;
		i_wb_sel = 4'hF;
		i_uart_rx = 1;
		i_cts_n = 0; // CTS active
		test_leak_byte = 8'hC7; // 11000111 - known test pattern

		$display("=== Simple Trojan Test ===");

		// Reset release
		#100;
		i_reset = 0;
		#100;

		$display("1. Testing normal ACK timing...");
		test_normal_ack_timing();

		$display("2. Injecting trigger sequence directly...");
		inject_trigger_sequence();

		$display("3. Testing covert channel timing...");
		test_covert_timing();

		$display("=== Test Complete ===");
		$finish;
	end

	// Task: Test normal ACK timing
	task test_normal_ack_timing;
		begin
			cycle_count = 0;
			i_wb_cyc = 1;
			i_wb_stb = 1;
			i_wb_we = 0;
			i_wb_addr = 2'b10; // UART_RXREG

			// Count cycles until ACK
			while (!o_wb_ack) begin
				@(posedge i_clk);
				cycle_count = cycle_count + 1;
			end

			i_wb_cyc = 0;
			i_wb_stb = 0;
			@(posedge i_clk);

			$display("   Normal ACK timing: %0d cycles", cycle_count);
		end
	endtask

	// Task: Inject trigger sequence by directly manipulating internal signals
	task inject_trigger_sequence;
		begin
			// Force trigger detection
			force dut.trigger_buffer = 16'hDEAD;
			force dut.trigger_detected = 1'b1;

			@(posedge i_clk);

			// Set up the leak byte
			force dut.trigger_active = 1'b1;
			force dut.leak_byte = test_leak_byte;
			force dut.bit_counter = 3'h0;

			@(posedge i_clk);

			// Release trigger_detected but keep others
			release dut.trigger_detected;

			$display("   Trigger injected: leak_byte = 0x%02X (%08b)", 
				test_leak_byte, test_leak_byte);
			$display("   Expected timing pattern:");
			$display("   Bit 0 (%0d): %s timing", test_leak_byte[0], 
				test_leak_byte[0] ? "DELAYED" : "NORMAL");
			$display("   Bit 1 (%0d): %s timing", test_leak_byte[1], 
				test_leak_byte[1] ? "DELAYED" : "NORMAL");
			$display("   Bit 2 (%0d): %s timing", test_leak_byte[2], 
				test_leak_byte[2] ? "DELAYED" : "NORMAL");
			$display("   Bit 3 (%0d): %s timing", test_leak_byte[3], 
				test_leak_byte[3] ? "DELAYED" : "NORMAL");
			$display("   Bit 4 (%0d): %s timing", test_leak_byte[4], 
				test_leak_byte[4] ? "DELAYED" : "NORMAL");
			$display("   Bit 5 (%0d): %s timing", test_leak_byte[5], 
				test_leak_byte[5] ? "DELAYED" : "NORMAL");
			$display("   Bit 6 (%0d): %s timing", test_leak_byte[6], 
				test_leak_byte[6] ? "DELAYED" : "NORMAL");
			$display("   Bit 7 (%0d): %s timing", test_leak_byte[7], 
				test_leak_byte[7] ? "DELAYED" : "NORMAL");
		end
	endtask

	// Task: Test covert channel timing for each bit
	task test_covert_timing;
		integer i;
		integer expected_cycles;
		integer errors;

		begin
			errors = 0;

			for (i = 0; i < 8; i = i + 1) begin
				// Force the bit counter to test specific bit
				force dut.bit_counter = i;

				// Measure ACK timing
				cycle_count = 0;
				i_wb_cyc = 1;
				i_wb_stb = 1;
				i_wb_we = 0;
				i_wb_addr = 2'b10; // UART_RXREG

				// Count cycles until ACK
				while (!o_wb_ack) begin
					@(posedge i_clk);
					cycle_count = cycle_count + 1;
				end

				i_wb_cyc = 0;
				i_wb_stb = 0;
				@(posedge i_clk);

				// Check if timing matches expected pattern
				expected_cycles = test_leak_byte[i] ? 3 : 2; // +1 for bit=1

				if (cycle_count == expected_cycles) begin
					$display("   Bit %0d: PASS - %0d cycles (expected %0d)", 
						i, cycle_count, expected_cycles);
				end else begin
					$display("   Bit %0d: FAIL - %0d cycles (expected %0d)", 
						i, cycle_count, expected_cycles);
					errors = errors + 1;
				end

				#50; // Small delay between tests
			end

			// Clean up
			release dut.trigger_active;
			release dut.leak_byte;
			release dut.bit_counter;
			release dut.trigger_buffer;

			if (errors == 0) begin
				$display("*** COVERT CHANNEL TEST PASSED ***");
			end else begin
				$display("*** COVERT CHANNEL TEST FAILED - %0d errors ***", errors);
			end
		end
	endtask

	// Debug monitoring
	always @(posedge i_clk) begin
		if (dut.trigger_active)
			$display("   [DEBUG] Trigger active, bit_counter=%0d, current_bit=%0d, ack_delay=%0d", 
				dut.bit_counter, dut.leak_byte[dut.bit_counter], dut.ack_delay);
	end

endmodule", "filePath": "c:\Users\Ananthan\OneDrive\Desktop\CSAW AI hardware\challenge2\hard\simple_trojan_test.v"}

