	reg		i_reset;

	// Wishbone interface
	reg		i_wb_cyc, i_wb_stb, i_wb_we;
	reg	[1:0]	i_wb_addr;
	reg	[31:0]	i_wb_data;
	reg	[3:0]	i_wb_sel;
